package com.epam.jmp.bank.impl;import java.util.HashMap;import java.util.Map;import java.util.UUID;import java.util.function.Supplier;import com.epam.bank.api.Bank;import com.epam.jmp.dto.BankCard;import com.epam.jmp.dto.BankCardType;import com.epam.jmp.dto.CreditBankCard;import com.epam.jmp.dto.DebitBankCard;import com.epam.jmp.dto.User;public class BankImpl implements Bank {    private final Map<BankCardType, Supplier<BankCard>> creators;    public BankImpl() {        creators = new HashMap<>();        creators.put(BankCardType.CREDIT, () -> {            return null;        });        creators.put(BankCardType.DEBIT, () -> {            return null;        });    }    @Override    public BankCard createBankCard(User user, BankCardType bankCardType) {      String uuid = UUID.randomUUID().toString();        switch (bankCardType){            case DEBIT -> {                return new DebitBankCard(uuid, user);            }            case CREDIT -> {                return new CreditBankCard(uuid, user);            }            default -> throw new IllegalStateException("unknown credit card type");        }    }}