package com.epam.service.api;import java.time.LocalDate;import java.util.List;import java.util.Optional;import com.epam.jmp.dto.BankCard;import com.epam.jmp.dto.Subscription;import com.epam.jmp.dto.User;public interface Service {    void subscribe(BankCard bankCard);    Optional<Subscription> getSubscriptionByCardNumber(String number);    List<User> getAllUsers();    default double getAverageUsersAge() {       return getAllUsers().stream().mapToDouble( user -> user.getBirthDay().toEpochDay())            .average()            .orElse(0);    }    default boolean isPayableUser(User user) {        LocalDate birthday =  user.getBirthDay();        return birthday.isBefore(LocalDate.now().minusYears(18));    }}